// login.component.ts
import { Component } from '@angular/core';
import { Router } from '@angular/router'; // Importa el servicio Router
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  email: string = '';
  password: string = '';

  constructor(private authService: AuthService, private router: Router) {} // Inyecta el servicio Router en el constructor


  reloadPage(): void {
    // Utilizamos el objeto 'location' para recargar la página
    location.reload();
  }

  async login(): Promise<void> {
    const success = await this.authService.login(this.email, this.password);

    if (success) {
      // Redirige al usuario a "/home" después del inicio de sesión exitoso
      // (Recuerda configurar el enrutamiento en tu aplicación)
      this.router.navigate(['/home']);
    } else {
      alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
    }
  }
}
