import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-reset-password',
  templateUrl: './reset-password.component.html',
  styleUrls: ['./reset-password.component.css'],
})
export class ResetPasswordComponent implements OnInit {
  token: string = ''; // Inicializar token con un valor vacío
  password: string = '';
  validatePassword: string = '';
  passwordMatch: boolean = false;
  resetStatus: string = '';
  hidePassword: boolean = true;
  showPasswordIcon: string = '../../assets/eye.svg';
  hideValidatePassword: boolean = true;
  showValidatePasswordIcon: string = '../../assets/eye.svg';
  showAlert: boolean = false;
  alertType: 'success' | 'error' = 'success';
  alertMessage: string = '';


  constructor(private route: ActivatedRoute, private http: HttpClient, private router: Router) {}

  ngOnInit(): void {
    this.route.queryParams.subscribe((params) => {
      this.token = params['token'];
    });
  }

  async resetPassword() {
    if (this.token && this.password && this.validatePassword) {
      if (this.password.length !== 4 || this.validatePassword.length !== 4) {
        this.showAlertMessage('La clave debe contener 4 dígitos.', 'error');
      } else if (this.password !== this.validatePassword) {
        this.showAlertMessage('Las claves no coinciden.', 'error');
      } else {
        const resetData = { token: this.token, password: this.password };
        try {
          await this.http.post('http://localhost:3000/api/cambiar-contrasena', resetData).toPromise();
          this.showAlertMessage('Clave restablecida exitosamente.', 'success');
          this.router.navigate(['/login']); // Redirige al usuario a la página de inicio de sesión
        } catch (error) {
          console.error('Error al restablecer la clave:', error);
          this.showAlertMessage('Error al restablecer la clave.', 'error');
        }
      }
    } else {
      this.showAlertMessage('Hay uno o más campos que están vacios.', 'error');
    }
  }

  onPasswordInput(event: any) {
    // Limitar el input de contraseña a solo 4 dígitos numéricos
    const passwordValue = event.target.value;
    event.target.value = passwordValue.replace(/\D/g, '').slice(0, 4);
    this.password = event.target.value;
    this.checkPasswordMatch();
  }

  onValidatePasswordInput(event: any) {
    // Limitar el input de validación de contraseña a solo 4 dígitos numéricos
    const validatePasswordValue = event.target.value;
    event.target.value = validatePasswordValue.replace(/\D/g, '').slice(0, 4);
    this.validatePassword = event.target.value;
    this.checkPasswordMatch();
  }

  checkPasswordMatch() {
    // Verificar si las contraseñas coinciden
    this.passwordMatch = this.password === this.validatePassword;
  }

  togglePasswordVisibility() {
    this.hidePassword = !this.hidePassword;
    this.showPasswordIcon = this.hidePassword ? '../../assets/eye.svg' : '../../assets/eye-slash.svg';
  }

  toggleValidatePasswordVisibility() {
    this.hideValidatePassword = !this.hideValidatePassword;
    this.showValidatePasswordIcon = this.hideValidatePassword ? '../../assets/eye.svg' : '../../assets/eye-slash.svg';
  }

  showAlertMessage(message: string, type: 'success' | 'error') {
    this.alertMessage = message;
    this.alertType = type;
    this.showAlert = true;

    setTimeout(() => {
      this.showAlert = false;
    }, 5000);
  }
}
<div class="container">
  <form class="login-form" (ngSubmit)="resetPassword()" #resetForm="ngForm">
    <div class="tittle-container">
      <h1 class="login-title">Restablecer clave</h1>
    </div>      
    <div class="alert-message" *ngIf="showAlert">
      {{ alertMessage }}
    </div>
    <div class="input-container">
      <div class="password-input-container">
        <input type="{{ hidePassword ? 'password' : 'text' }}" placeholder="Clave nueva" name="password" id="password" [(ngModel)]="password" class="login-input" (input)="onPasswordInput($event)" required pattern="[0-9]{4}">
        <span class="password-toggle-btn" (click)="togglePasswordVisibility()">
          <img [src]="showPasswordIcon" alt="Toggle Password">
        </span>
      </div>
    </div>
    <div class="input-container">
      <div class="validatePassword-input-container">
        <input type="{{ hideValidatePassword ? 'password' : 'text' }}" placeholder="Confirmar clave nueva" name="validatePassword" id="validatePassword" [(ngModel)]="validatePassword" class="login-input" (input)="onValidatePasswordInput($event)" required pattern="[0-9]{4}">
        <span class="password-toggle-btn" (click)="toggleValidatePasswordVisibility()">
          <img [src]="showValidatePasswordIcon" alt="Toggle ValidatePassword">
        </span>
      </div>
    </div>
    <div class="input-container">
      <button type="submit" class="login-button" (click)="resetPassword()">Restablecer</button>
    </div>
  </form>
  <footer>
    <p>&copy; Desafío Técnico Chek Agosto 2023 - Claudio Andrés Bascuñán Valdivia</p>
  </footer>
</div>
