import { Component } from '@angular/core';

@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.component.html',
  styleUrls: ['./forgot-password.component.css']
})
export class ForgotPasswordComponent {
  phoneEmail: string = '';

  reloadPage(): void {
    // Utilizamos el objeto 'location' para recargar la página
    location.reload();
  }

  sendRecovery() {
    // Here, you can add the logic to send the recovery email or SMS.
    // For this example, let's just show an alert.

    // Assuming you are using a service to send the recovery request
    // and it returns a Promise or Observable:
    // recoveryService.sendRecoveryRequest(this.phoneEmail).subscribe(() => {
    //   alert('Se ha enviado un correo o SMS al teléfono.');
    // });

    // For the sake of simplicity, let's just show an alert directly.
    alert('Se ha enviado un correo eléctronico o SMS al celular para reestrablecer la contraseña.');
  }
}
