import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

interface RegisterUser {
  firstname: string;
  lastname: string;
  email: string;
  password: string;
  validatePassword: string;
  birthdate: string;
}

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  // Variables para almacenar los datos del formulario
  firstname: string = '';
  lastname: string = '';
  email: string = '';
  password: string = '';
  validatePassword: string = '';
  birthdate: string = '';

  showAlert: boolean = false; // Variable para controlar la visibilidad del texto de alerta
  passwordPattern: RegExp = /^\d{4}$/; // Expresión regular para validar la contraseña (4 dígitos)

  constructor(private http: HttpClient, private router: Router) { }

  // Función para enviar la solicitud de registro
  async registerUser() {
    if (!this.validateForm()) {
      return; // Si hay campos vacíos o las contraseñas no coinciden, detener el registro
    }

    const user: RegisterUser = {
      firstname: this.firstname,
      lastname: this.lastname,
      email: this.email,
      password: this.password,
      validatePassword: this.validatePassword,
      birthdate: this.birthdate
    };

    try {
      await this.http.post<any>('http://localhost:3000/api/register', user).toPromise();
      alert('Cuenta creada exitosamente! Verifica tu correo electrónico para activar tu cuenta.');
      this.router.navigate(['/login']);
    } catch (error: any) {
      console.error('Error al registrar usuario:', error);
      alert(error.error.message);
      // Aquí puedes mostrar un mensaje de error al usuario
    }
  }

  // Función para validar el formulario antes de enviarlo
  validateForm(): boolean {
    if (
      this.firstname.trim() === '' ||
      this.lastname.trim() === '' ||
      this.email.trim() === '' ||
      this.password.trim() === '' ||
      this.validatePassword.trim() === '' ||
      this.birthdate.trim() === ''
    ) {
      alert('Todos los campos son obligatorios');
      return false;
    }

    if (this.firstname.length > 20) {
      alert('El nombre no puede superar los 20 caracteres');
      return false;
    }

    if (this.lastname.length > 20) {
      alert('El apellido no puede superar los 20 caracteres');
      return false;
    }

    if (!this.isValidDate(this.birthdate)) {
      alert('Fecha de nacimiento inválida. El formato debe ser yyyy-mm-dd');
      return false;
    }

    if (!this.email.includes('@') || !this.email.includes('.')) {
      alert('El correo electrónico no es válido');
      return false;
    }

    if (!this.passwordPattern.test(this.password)) {
      alert('La contraseña debe ser de 4 dígitos numéricos');
      return false;
    }

    if (this.password !== this.validatePassword) {
      alert('Las contraseñas no coinciden');
      return false;
    }

    return true;
  }

  // Función para validar el formato de la fecha
  isValidDate(dateString: string): boolean {
    const regexDate = /^\d{4}-\d{2}-\d{2}$/;
    if (!regexDate.test(dateString)) return false;
    const date = new Date(dateString);
    return date.toISOString().slice(0, 10) === dateString;
  }
}
