import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { catchError, map, tap } from 'rxjs/operators';
import { throwError } from 'rxjs';
import { AngularFireAuth } from '@angular/fire';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  email: string = '';
  password: string = '';
  hidePassword: boolean = true;
  showPasswordIcon: string = '../../assets/eye.svg';

  constructor(private http: HttpClient, private router: Router, private auth: AngularFireAuth) {}

  async login(): Promise<void> {
    if (this.email && this.password) {
      try {
        const response = await this.http.post<{ token: string }>('http://localhost:3000/api/login', { email: this.email, password: this.password })
          .pipe(
            tap(response => console.log('Token JWT:', response.token)),
            map(response => response.token),
            catchError(error => {
              console.error('Error de inicio de sesión:', error);
              alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
              return throwError(error);
            })
          )
          .toPromise();

        if (response) {
          // Guarda el token en el LocalStorage
          localStorage.setItem('access_token', response);

          // Redirige al usuario a "/home" después del inicio de sesión exitoso
          this.router.navigate(['/home']);
        } else {
          console.error('Respuesta vacía del servidor');
          alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
        }
      } catch (error) {
        console.error('Error de inicio de sesión:', error);
        alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
      }
    } else {
      alert('Por favor, ingrese usuario y contraseña válidos');
    }
  }

  togglePasswordVisibility() {
    this.hidePassword = !this.hidePassword;
    this.showPasswordIcon = this.hidePassword ? '../../assets/eye.svg' : '../../assets/eye-slash.svg';
  }

  loginWithGoogle() {
    this.auth
      .signInWithPopup(new firebase.auth.GoogleAuthProvider())
      .then((result) => {
        // Si el inicio de sesión es exitoso, puedes redirigir al usuario o realizar otras acciones
        console.log('Inicio de sesión exitoso', result.user);
      })
      .catch((error) => {
        // Manejo de errores en caso de que ocurra algún problema
        console.error('Error en el inicio de sesión', error);
      });
  }


}
