// login.component.ts

import { Component } from '@angular/core';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  username: string = '';
  password: string = '';

  constructor(private authService: AuthService) { }

  onLogin(): void {
    // Llamar al servicio de autenticación para realizar el inicio de sesión
    this.authService.login(this.username, this.password);
  }
}
<div class="container">
  <mat-card>
    <mat-card-header>
      <mat-card-title>Login</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <form (submit)="onSubmit()">
        <mat-form-field>
          <input matInput placeholder="Usuario" [(ngModel)]="username" name="username" required>
        </mat-form-field>

        <mat-form-field>
          <input matInput type="password" placeholder="Contraseña" [(ngModel)]="password" name="password" required>
        </mat-form-field>

        <button mat-raised-button color="primary" type="submit">Ingresar</button>
      </form>
    </mat-card-content>
  </mat-card>
</div>
