import { Injectable } from '@angular/core';
import { CanActivate, Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class AuthGuard implements CanActivate {
  constructor(private router: Router) {}

  canActivate(): boolean {
    // Verifica si el token de acceso está presente en el LocalStorage
    const accessToken = localStorage.getItem('access_token');

    if (accessToken) {
      // Si hay un token de acceso, el usuario está autenticado y se permite el acceso a la ruta
      return false;
    } else {
      // Si no hay un token de acceso, el usuario no está autenticado y se redirige al login
      this.router.navigate(['/login']);
      return true;
    }
  }
}
