import { Component, ElementRef, ViewChild } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { catchError, map, tap } from 'rxjs/operators';
import { throwError } from 'rxjs';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  email: string = '';
  password: string = '';
  @ViewChild('emailInput') emailInput!: ElementRef<HTMLInputElement>;
  @ViewChild('passwordInput') passwordInput!: ElementRef<HTMLInputElement>;

  constructor(private http: HttpClient, private router: Router) {}

  ngAfterViewInit(): void {
    // Enfocar automáticamente el campo de email al cargar la página
    this.emailInput.nativeElement.focus();
  }

  async login(): Promise<void> {
    if (this.email && this.password) {
      try {
        const response = await this.http.post<{ token: string }>('http://localhost:3000/api/login', { email: this.email, password: this.password })
          .pipe(
            tap(response => console.log('Token JWT:', response.token)),
            map(response => response.token),
            catchError(error => {
              console.error('Error de inicio de sesión:', error);
              alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
              return throwError(error);
            })
          )
          .toPromise();

        if (response) {
          // Guarda el token en el LocalStorage
          localStorage.setItem('access_token', response);

          // Redirige al usuario a "/home" después del inicio de sesión exitoso
          this.router.navigate(['/home']);
        } else {
          console.error('Respuesta vacía del servidor');
          alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
        }
      } catch (error) {
        console.error('Error de inicio de sesión:', error);
        alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
      }
    } else {
      alert('Por favor, ingrese usuario y contraseña válidos');
    }
  }
}
<div class="login-container">
  <div class="image-overlay"></div> <!-- Capa de fondo degradado -->
  <div class="center-form">
    <div class="image-container">
      <img class="image-svg" src="../../assets/logoChek.svg" alt="Imagen SVG"> <!-- Imagen SVG encima del formulario -->
    </div>
    <label class="login-label">Ingresa</label> <!-- Label "Login" con letras blancas -->
    <form>
      <div class="form-field">
        <input #emailInput type="text" [(ngModel)]="email" placeholder="Correo electrónico o teléfono" id="email" />
      </div>
      <div class="form-field">
        <input #passwordInput type="password" [(ngModel)]="password" placeholder="Clave" id="password" />
      </div>
      <div class="center-login-button"> <!-- Contenedor del botón centrado -->
        <button class="button-sign-in" (click)="login()">Ingresar</button>
      </div>
      <div class="forgot-password"> <!-- Contenedor del label "¿Olvidaste tu clave?" -->
        <label class="forgot-password-label-login" routerLink="/forgot-password">Recuperar contraseña</label>
      </div>
      <div class="divider">
        <hr>
      </div>
      <div class="center-login-button"> <!-- Contenedor del botón centrado -->
        <button class="button-register" routerLink="/register">Crear cuenta nueva</button>
      </div>
    </form>
  </div>
</div>
