// login.component.ts

import { Component } from '@angular/core';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  username: string = '';
  password: string = '';

  constructor(private authService: AuthService) { }

  onLogin(): void {
    // Llamar al servicio de autenticación para realizar el inicio de sesión
    this.authService.login(this.username, this.password);
  }
}
<div class="login-container">
  <div class="image-overlay"></div> <!-- Capa de fondo degradado -->
  <div class="center-form">
    <div class="image-container">
      <img class="image-svg" src="../../assets/logoChek.svg" alt="Imagen SVG"> <!-- Imagen SVG encima del formulario -->
    </div>
    <form>
      <mat-form-field>
        <input matInput placeholder="Usuario" name="username">
      </mat-form-field>
      <mat-form-field>
        <input matInput type="password" placeholder="Contraseña" name="password">
      </mat-form-field>
      <div class="center-button"> <!-- Contenedor del botón centrado -->
        <button mat-raised-button color="primary">Iniciar sesión</button>
      </div>
    </form>
  </div>
</div>
