// login.component.ts

import { Component } from '@angular/core';
import { AuthService } from '../auth.service';

declare const gapi: any;

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {


  private clientId: string = 'YOUR_GOOGLE_CLIENT_ID';

  ngOnInit() {
    this.loadGooglePlatform();
  }

  loadGooglePlatform() {
    gapi.load('auth2', () => {
      gapi.auth2.init({
        client_id: this.clientId
      });
    });
  }

  signInWithGoogle() {
    const auth2 = gapi.auth2.getAuthInstance();
    auth2.signIn().then((user: any) => {
      // Handle the Google login response here (e.g., send the ID token to your server for authentication)
      console.log('Google user:', user);
    }, (error: any) => {
      console.error('Error signing in with Google:', error);
    });
  }

  username: string = '';
  password: string = '';

  constructor(private authService: AuthService) { }

  onLogin(): void {
    // Llamar al servicio de autenticación para realizar el inicio de sesión
    this.authService.login(this.username, this.password);
  }
}
<div class="login-container">
  <div class="image-overlay"></div> <!-- Capa de fondo degradado -->
  <div class="center-form">
    <div class="image-container">
      <img class="image-svg" src="../../assets/logoChek.svg" alt="Imagen SVG"> <!-- Imagen SVG encima del formulario -->
    </div>
    <label class="login-label">Login</label> <!-- Label "Login" con letras blancas -->
    <form>
      <mat-form-field>
        <input matInput placeholder="Correo electrónico o teléfono" name="username">
      </mat-form-field>
      <mat-form-field>
        <input matInput type="password" placeholder="Clave" name="password">
      </mat-form-field>
      <div class="center-button"> <!-- Contenedor del botón centrado -->
        <button class="button-sign-in" mat-raised-button color="primary">Ingresar</button>
      </div>
      <div class="forgot-password"> <!-- Contenedor del label "¿Olvidaste tu clave?" -->
        <label class="forgot-password-label">¿Olvidaste tu clave?</label>
      </div>
      <div class="divider">
        <hr>
      </div>
      <button mat-raised-button color="primary" (click)="signInWithGoogle()">
        <img src="https://www.gstatic.com/webp/gallery/2.jpg" alt="Google Logo" style="height: 24px; margin-right: 8px;">
        Sign in with Google
      </button>
    </form>
  </div>
</div>
