import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { catchError, map, tap } from 'rxjs/operators';
import { throwError } from 'rxjs';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  email: string = '';
  password: string = '';

  constructor(private http: HttpClient, private router: Router) {}

  async login(): Promise<void> {
    if (this.email && this.password) {
      try {
        const response = await this.http.post<{ token: string }>('http://localhost:3000/api/login', { email: this.email, password: this.password })
          .pipe(
            tap(response => console.log('Token JWT:', response.token)),
            map(response => response.token),
            catchError(error => {
              console.error('Error de inicio de sesión:', error);
              alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
              return throwError(error);
            })
          )
          .toPromise();

        if (response) {
          // Guarda el token en el LocalStorage
          localStorage.setItem('access_token', response);

          // Redirige al usuario a "/home" después del inicio de sesión exitoso
          this.router.navigate(['/home']);
        } else {
          console.error('Respuesta vacía del servidor');
          alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
        }
      } catch (error) {
        console.error('Error de inicio de sesión:', error);
        alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
      }
    } else {
      alert('Por favor, ingrese usuario y contraseña válidos');
    }
  }
}
<div class="container">
    <form class="login-form">
      <div class="logo-container">
        <img src="../../assets/logoChek.svg" alt="Imagen SVG" class="logo-img">
      </div>
      <div class="tittle-container">
        <h1 class="login-title">Inicia sesión</h1>
      </div>      
      <div class="input-container">
        <input type="text" placeholder="Correo electrónico o teléfono" name="email" id="email" [(ngModel)]="email" class="login-input">
      </div>

      <div class="input-container">
        <input type="password" placeholder="Clave" name="password" id="password" [(ngModel)]="password" class="login-input">
      </div>

      <div class="input-container">
        <button (click)="login()" class="login-button">Ingresar</button>
      </div>

      <div class="forgot-password">
        <label routerLink="/forgot-password" class="forgot-password-link">Recuperar contraseña</label>
      </div>
      <div class="create-account">
        <button routerLink="/register" class="create-account-button">Crear cuenta nueva</button>
      </div>
    </form>
</div>
