import { Component, HostListener, OnInit } from '@angular/core';
import { Router } from '@angular/router';

interface LogData {
  date_time: string;
  status: string;
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  firstname: string | null = null;
  logs: LogData[] = [];
  private sessionTimeout = 5 * 60 * 1000; // 5 minutos en milisegundos
  private timeoutId: any; // Usa 'any' para el tipo del temporizador
  private isPageReloading = false;
  currentPage: number = 1;
  pageSize: number = 10;
  totalPages: number = 1;

  constructor(private router: Router) {}

  ngOnInit(): void {
    window.addEventListener('unload', () => {
      if (!this.isPageReloading) {
        this.logout();
      }
    });

    // Configurar un escuchador para el evento 'keydown' para detectar una recarga de página (F5)
    window.addEventListener('keydown', (event) => {
      if (event.key === 'F5') {
        this.isPageReloading = true;
      }
      this.onActivity();
    });

    window.addEventListener('click', () => {
      this.onActivity();
    });

    // Obtener el firstname y los logs desde el token almacenado en el LocalStorage
    const token = localStorage.getItem('access_token');
    if (token) {
      const tokenData = JSON.parse(atob(token.split('.')[1]));
      this.firstname = tokenData.firstname;
      this.logs = tokenData.logs || []; // Si el token no contiene datos de logs, se establece un arreglo vacío
    }

    this.calculateTotalPages();
    this.startSessionTimeout();
  }

  calculateTotalPages(): void {
    this.totalPages = Math.ceil(this.logs.length / this.pageSize);
  }

  goToPage(pageNumber: number): void {
    if (pageNumber >= 1 && pageNumber <= this.totalPages) {
      this.currentPage = pageNumber;
    }
  }

  getPaginatedLogs(): LogData[] {
    const startIndex = (this.currentPage - 1) * this.pageSize;
    const endIndex = startIndex + this.pageSize;
    return this.logs.slice(startIndex, endIndex);
  }

  logout(): void {
    // Elimina el token de acceso del LocalStorage
    localStorage.removeItem('access_token');

    setTimeout(() => {
      alert('Se ha cerrado sesión.');
    }, 500);
    // Muestra una alerta de cierre de sesión si el usuario cierra sesión manualmente

    // Redirige al usuario a la página de inicio de sesión ("/login") después de cerrar sesión
    this.router.navigate(['/login']);
  }

  onActivity(): void {
    clearTimeout(this.timeoutId); // Reinicia el temporizador
    this.startSessionTimeout(); // Establece el nuevo temporizador
  }

  startSessionTimeout(): void {
    this.timeoutId = setTimeout(() => this.logout(), this.sessionTimeout);
  }
}
<div>
  <h1>Bienvenido, {{ firstname }}!</h1>
  <button class="logout-button" (click)="logout()">Cerrar Sesión</button>
</div>

<form class="form-container">
  <div class="table-container">
    <table>
      <caption>Historial de acceso</caption>
      <thead>
        <tr>
          <th>Fecha</th>
          <th>Estado</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let log of getPaginatedLogs()">
          <td>{{ log.date_time }}</td>
          <td>{{ log.status }}</td>
        </tr>
      </tbody>
    </table>

    <!-- Elementos de paginación -->
    <div class="pagination">
      <button (click)="goToPage(1)" [disabled]="currentPage === 1">Primera</button>
      <button (click)="goToPage(currentPage - 1)" [disabled]="currentPage === 1">Anterior</button>
      <span>Página {{ currentPage }} de {{ totalPages }}</span>
      <button (click)="goToPage(currentPage + 1)" [disabled]="currentPage === totalPages">Siguiente</button>
      <button (click)="goToPage(totalPages)" [disabled]="currentPage === totalPages">Última</button>
    </div>
  </div>
</form>
