// login.component.ts

import { Component } from '@angular/core';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  username: string = '';
  password: string = '';

  constructor(private authService: AuthService) { }

  onLogin(): void {
    // Llamar al servicio de autenticación para realizar el inicio de sesión
    this.authService.login(this.username, this.password);
  }
}
<div class="login-container">
  <h2>Iniciar sesión</h2>
  <mat-form-field appearance="fill">
    <mat-label>Nombre de usuario</mat-label>
    <input matInput placeholder="Nombre de usuario" [(ngModel)]="username">
  </mat-form-field>
  <mat-form-field appearance="fill">
    <mat-label>Contraseña</mat-label>
    <input matInput type="password" placeholder="Contraseña" [(ngModel)]="password">
  </mat-form-field>
  <button mat-raised-button color="primary" (click)="onLogin()">Iniciar sesión</button>
</div>
