import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.component.html',
  styleUrls: ['./forgot-password.component.css'],
})
export class ForgotPasswordComponent {
  email: string = '';
  resetStatus: string = '';

  constructor(private http: HttpClient) {}

  async sendRecovery(): Promise<void> {
    if (this.email) {
      const resetData = { email: this.email };
      try {
        const response = await this.http.post<any>('http://localhost:4200/api/reset-password', resetData).toPromise();
        this.resetStatus = 'Se ha enviado un correo electrónico para restablecer la contraseña.';
      } catch (error) {
        console.error('Error al enviar el correo:', error);
        this.resetStatus = 'Ha ocurrido un error al enviar el correo.';
      }
    } else {
      this.resetStatus = 'Por favor, ingresa un correo electrónico válido.';
    }
  }
}
