import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-validate-register',
  templateUrl: './validate-register.component.html',
  styleUrls: ['./validate-register.component.css'],
})

export class ValidateRegisterComponent implements OnInit {
  constructor(private route: ActivatedRoute, private http: HttpClient, private router: Router) {}

  ngOnInit() {
    this.route.queryParams.subscribe(params => {
      const token = params['token'];

      if (token) {
        this.verifyEmail(token);
      } else {
        this.router.navigate(['/']);
      }
    });
  }

  async verifyEmail(token: string) {
    try {
      const response = await this.http.get<any>(`http://localhost:3000/api/verify-email?token=${token}`).toPromise();
    } catch (error) {
      alert('Ha ocurrido un error al validar su cuenta.');
      this.router.navigate(['/']);
    }
  }
}
