import { Component } from '@angular/core';

@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.component.html',
  styleUrls: ['./forgot-password.component.css']
})
export class ForgotPasswordComponent {
  phoneEmail: string = '';

  sendRecovery() {
    alert('Se ha enviado un correo eléctronico o SMS al celular para reestrablecer la contraseña.');
  }
}
<div class="container">
  <form class="login-form">
    <div class="logo-container">
      <img src="../../assets/logoChek.svg" alt="Imagen SVG" class="logo-img">
    </div>
    <div class="tittle-container">
      <h1 class="forgot-password-title">Recupera tu contraseña</h1>
    </div>
      <div>
        <input type="text" placeholder="Correo eléctronico o teléfono" name="phoneEmail" id="phoneEmail" [(ngModel)]="phoneEmail">
      </div>
      <div>
          <button (click)="sendRecovery()" routerLink="/">Enviar</button>
      </div>
    </form>
</div>
