// auth.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { catchError, map, tap } from 'rxjs/operators';
import { throwError } from 'rxjs';
import { Router } from '@angular/router'; // Importa el servicio Router

interface LoginResponse {
  token: string;
}

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private isLoggedIn = false;
  private email = ''; // Almacena el nombre de usuario o las credenciales aquí

  constructor(private http: HttpClient) {}

  async login(email: string, password: string): Promise<boolean> {
    if (email && password) {
      try {
        const response = await this.http
          .post<LoginResponse>('http://localhost:3000/api/login', { email, password })
          .pipe(
            tap(response => console.log('Token JWT:', response.token)),
            map(response => response.token),
            catchError(error => {
              console.error('Error de inicio de sesión:', error);
              return throwError(error);
            })
          )
          .toPromise();

        if (response) {
          this.email = email; // Podrías guardar el nombre de usuario o las credenciales aquí si lo deseas
          this.isLoggedIn = true;
          this.router.navigate(['/home']);
          // También, aquí normalmente guardarías el token o la información del usuario en LocalStorage o SessionStorage.
          return true;
        } else {
          console.error('Respuesta vacía del servidor');
          return false;
        }
      } catch (error) {
        console.error('Error de inicio de sesión:', error);
        return false;
      }
    } else {
      return false;
    }
  }

  logout() {
    this.isLoggedIn = false;
    this.email = '';
    // Aquí, normalmente eliminarías el token o la información del usuario almacenada.
  }

  isAuthenticated(): boolean {
    return this.isLoggedIn;
  }

  getUsername(): string {
    return this.email;
  }
}
