import { Injectable } from '@angular/core';
import { CanActivate, Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class PreventBackGuard implements CanActivate {
  constructor(private router: Router) {}

  canActivate(): boolean {
    // Verifica si el token de acceso está presente en el LocalStorage
    const accessToken = localStorage.getItem('access_token');

    if (accessToken) {
      // Si hay un token de acceso, el usuario está autenticado y no se permite retroceder
      this.router.navigate(['/home']);
      return false;
    } else {
      // Si no hay un token de acceso, el usuario no está autenticado y se permite retroceder
      return true;
    }
  }
}
