import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  firtsname: string = '';
  lastname: string = '';
  email: string = '';
  password: string = '';
  phone: string = '';
  validatePassword: string = '';
  birthdate: string = '';

  constructor(private http: HttpClient) {}

  register() {
    const newUser = { firstname: this.firtsname, lastname: this.lastname, email: this.email, password: this.password, phone: this.phone, birthdate: this.birthdate};
    this.http.post('http://localhost:3000/api/register', newUser)
      .subscribe(
        () => alert('Registro exitoso'),
        (error) => alert('Error al registrar: ' + error.error.message)
      );
  }
}
<div class="register-container">
  <div class="image-overlay"></div> <!-- Capa de fondo degradado -->
  <div class="center-form">
    <div class="logo-container">
      <img class="image-svg" src="../../assets/logoChek.svg" alt="Imagen SVG">
    </div>
    <label class="register-label">Register</label> <!-- Label "Register" con letras blancas -->
    <form>
      <div class="form-field">
        <input type="text" placeholder="Nombre" name="firtsname" id="firtsname" [(ngModel)]="firtsname">
      </div>
      <div class="form-field">
        <input type="text" placeholder="Apellido" name="lastname" id="lastname" [(ngModel)]="lastname">
      </div>
      <div class="form-field">
        <input type="tel" placeholder="Número de celular" name="phone" id="phone" [(ngModel)]="phone">
      </div>
      <div class="form-field">
        <input type="email" placeholder="Correo electrónico" name="email" id="email" [(ngModel)]="email">
      </div>
      <div class="form-field">
        <input type="password" placeholder="Clave nueva" name="password" id="password" [(ngModel)]="password">
      </div>
      <div class="form-field">
        <input type="password" placeholder="Confirmar clave nueva" name="validatePassword" [(ngModel)]="validatePassword">
      </div>
      <div class="form-field">
        <input type="text" placeholder="Fecha de nacimiento" name="birthdate" [(ngModel)]="birthdate">
      </div>
      <div class="center-button"> <!-- Contenedor del botón centrado -->
        <button class="button-register" (click)="register()">Registrarse</button>
      </div>
    </form>
  </div>
</div>
