import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { catchError, map, tap } from 'rxjs/operators';
import { throwError } from 'rxjs';

interface LoginResponse {
  token: string;
}

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = '';
  password: string = '';

  constructor(private http: HttpClient, private router: Router) {}

  async login(): Promise<void> {
    if (this.username && this.password) {
      try {
        const response = await this.http.post<LoginResponse>('http://localhost:3000/api/login', { username: this.username, password: this.password })
          .pipe(
            tap(response => console.log('Token JWT:', response.token)),
            map(response => response.token),
            tap(token => localStorage.setItem('access_token', token)),
            catchError(error => {
              console.error('Error de inicio de sesión:', error);
              alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
              return throwError(error);
            })
          )
          .toPromise(); // Elimina esta línea, ya no necesitamos toPromise()

        if (response) {
          // Redirige al usuario a "/home" después del inicio de sesión exitoso
          this.router.navigate(['/home']);
        } else {
          console.error('Respuesta vacía del servidor');
          alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
        }
      } catch (error) {
        console.error('Error de inicio de sesión:', error);
        alert('Inicio de sesión fallido. Por favor, verifique sus credenciales.');
      }
    } else {
      alert('Por favor, ingrese usuario y contraseña válidos');
    }
  }
}
<div class="login-container">
  <div class="image-overlay"></div> <!-- Capa de fondo degradado -->
  <div class="center-form">
    <div class="image-container">
      <img class="image-svg" src="../../assets/logoChek.svg" alt="Imagen SVG"> <!-- Imagen SVG encima del formulario -->
    </div>
    <label class="login-label">Login</label> <!-- Label "Login" con letras blancas -->
    <form>
      <mat-form-field>
        <input matInput placeholder="Correo electrónico o teléfono" name="username" id="username" [(ngModel)]="username">
      </mat-form-field>
      <mat-form-field>
        <input matInput type="password" placeholder="Clave" name="password" id="password" [(ngModel)]="password">
      </mat-form-field>
      <div class="center-button"> <!-- Contenedor del botón centrado -->
        <button class="button-sign-in" mat-raised-button color="primary" (click)="login()">Ingresar</button>
      </div>
      <div class="forgot-password"> <!-- Contenedor del label "¿Olvidaste tu clave?" -->
        <label class="forgot-password-label">¿Olvidaste tu clave?</label>
      </div>
      <div class="divider">
        <hr>
      </div>
      <div class="center-button"> <!-- Contenedor del botón centrado -->
        <button class="button-register" mat-raised-button color="primary" routerLink="/register">Ingresar</button>
      </div>
    </form>
  </div>
</div>
